import React, { useState } from "react";
import "./App.css";

function App() {
  const [jd, setJd] = useState(null);
  const [resumes, setResumes] = useState([]);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleAnalyze = async () => {
    if (!jd || resumes.length === 0) {
      alert("Please upload Job Description and Resumes");
      return;
    }
    setLoading(true);
    const formData = new FormData();
    formData.append("jd", jd);
    resumes.forEach((file) => formData.append("resumes", file));

    try {
      const res = await fetch("/analyze/", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setResults(data.results);
    } catch (err) {
      console.error("Error:", err);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>AI Resume Screening</h1>

      <div className="upload-section">
        <label>Job Description:</label>
        <input type="file" onChange={(e) => setJd(e.target.files[0])} />
      </div>

      <div className="upload-section">
        <label>Resumes:</label>
        <input type="file" multiple onChange={(e) => setResumes([...e.target.files])} />
      </div>

      <button onClick={handleAnalyze}>Analyze</button>

      {loading && <p>Processing...</p>}

      {results && results.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Strengths</th>
              <th>Missing</th>
              <th>Primary Score</th>
              <th>Secondary Score</th>
              <th>Overall Score</th>
            </tr>
          </thead>
          <tbody>
            {results.map((r, idx) => (
              <tr key={idx}>
                <td>{r.Name}</td>
                <td>{r["Strengths"]}</td>
                <td>{r["Missing"]}</td>
                <td>{r["Primary Score"]}</td>
                <td>{r["Secondary Score"]}</td>
                <td>{r["Overall Score"]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !loading && <p>No results yet. Upload files and click Analyze.</p>
      )}
    </div>
  );
}

export default App;
