import React, { useState } from "react";
import axios from "axios";

function App() {
  const [jdFile, setJdFile] = useState(null);
  const [resumes, setResumes] = useState([]);
  const [results, setResults] = useState([]);  // ✅ Initialize as empty array
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("jd", jdFile);
    resumes.forEach((r) => formData.append("resumes", r));

    try {
      const response = await axios.post("http://localhost:8000/analyze/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // ✅ Defensive assignment
      setResults(response.data.results || []);
    } catch (error) {
      console.error("Error:", error);
      setResults([]); // prevent undefined
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>AI Resume Screening</h1>
      <form onSubmit={handleSubmit}>
        <label>Job Description:</label>
        <input type="file" onChange={(e) => setJdFile(e.target.files[0])} />
        <br />
        <label>Resumes:</label>
        <input type="file" multiple onChange={(e) => setResumes([...e.target.files])} />
        <br />
        <button type="submit">Analyze</button>
      </form>

      {loading && <p>Processing...</p>}

      {!loading && results && results.length > 0 && (
        <table border="1" cellPadding="8" style={{ marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Match %</th>
              <th>Strengths</th>
              <th>Weaknesses</th>
            </tr>
          </thead>
          <tbody>
            {results.map((r, i) => (
              <tr key={i}>
                <td>{r.Name}</td>
                <td>{r.match_score}</td>
                <td>{r.strengths}</td>
                <td>{r.weaknesses}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
